function findDatasetInfo(dictionary, datasetName) {
  // Convert dictionary column data into array of objects
  const dictionaryArray = Array.from({ length: dictionary[Object.keys(dictionary)[0]].length }, (_, i) =&gt; {
    return Object.fromEntries(Object.entries(dictionary).map(([key, values]) =&gt; [key, values[i]]));
